5621,{'l_kind': 'body', 'l_body': '(b) 50%, no mínimo, das ações em que se divide o capital '}
5622,{'l_kind': 'body', 'l_body': 'social fixado no estatuto.'}
5623,{'l_kind': 'body', 'l_body': '(C) 25%, no mínimo, das ações em que se divide o capital '}
5624,{'l_kind': 'body', 'l_body': 'social fixado no estatuto.'}
5625,{'l_kind': 'body', 'l_body': '(D) 10%, no mínimo, das ações em que se divide o capital '}
