9037,{'l_kind': 'body', 'l_body': '(a) nula. '}
9038,{'l_kind': 'body', 'l_body': '(b) anulável.'}
9039,{'l_kind': 'body', 'l_body': '(C) inexistente. '}
9040,{'l_kind': 'body', 'l_body': '(D) ineficaz.'}
9041,{'l_kind': 'body', 'l_body': '(e) incompleta.'}
