9799,{'l_kind': 'body', 'l_body': 'v. A ausência do condômino à assembleia, quando '}
9800,{'l_kind': 'body', 'l_body': 'regularmente comunicado, não impede que os efeitos '}
9801,{'l_kind': 'body', 'l_body': 'dela decorrentes lhe atinjam, equiparado aos demais '}
9802,{'l_kind': 'body', 'l_body': 'condôminos que compareceram ao ato. '}
9803,{'l_kind': 'body', 'l_body': 'Assinale: '}
