9805,{'l_kind': 'body', 'l_body': 'retas. '}
9806,{'l_kind': 'body', 'l_body': '(b) se somente as afirmativas I, II e III estiverem corretas. '}
9807,{'l_kind': 'body', 'l_body': '(C) se somente as afirmativas I e II estiverem corretas. '}
9808,{'l_kind': 'body', 'l_body': '(D) se somente as afirmativas I e V estiverem corretas. '}
9809,{'l_kind': 'body', 'l_body': '(e) se somente as afirmativas III e V estiverem corretas.'}
