12337,{'l_kind': 'body', 'l_body': '(D) Moralidade.'}
12338,{'l_kind': 'body', 'l_body': '(e) Publicidade.'}
12339,{'l_kind': 'body', 'l_body': 'O princípio da impessoalidade impõe respeito à igualdade, à neutralidade '}
