13979,{'l_kind': 'body', 'l_body': '(C) 90,75%.'}
13980,{'l_kind': 'body', 'l_body': '(D) 91,00%.'}
13981,{'l_kind': 'body', 'l_body': '(e) 90,50%.'}
13982,{'l_kind': 'body', 'l_body': 'Vide o art. 37, § 12, da CF, acrescido pela EC 47/2005. '}
