13985,{'l_kind': 'body', 'l_body': 'a:'}
13986,{'l_kind': 'body', 'l_body': '(a) diárias, ajuda de custo e transporte.'}
13987,{'l_kind': 'body', 'l_body': '(b) transporte, ajuda de custo e atividade insalubre.'}
13988,{'l_kind': 'body', 'l_body': '(C) serviço extraordinário, diárias e função de direção.'}
13989,{'l_kind': 'body', 'l_body': '(D) ajuda de custo, diárias e adicional noturno.'}
13990,{'l_kind': 'body', 'l_body': '(e) transporte, periculosidade e insalubridade.'}
