23336,{'l_kind': 'body', 'l_body': '(b) no prazo comum de 30 (trinta) dias, após as partes '}
23337,{'l_kind': 'body', 'l_body': 'serem intimadas da apresentação do laudo do perito '}
23338,{'l_kind': 'body', 'l_body': 'do juízo;'}
23339,{'l_kind': 'body', 'l_body': '(C) no prazo comum de 20 (vinte) dias, após as partes '}
23340,{'l_kind': 'body', 'l_body': 'serem intimadas da apresentação do laudo do perito '}
23341,{'l_kind': 'body', 'l_body': 'do juízo;'}
