23407,{'l_kind': 'body', 'l_body': 'recebimento.'}
23408,{'l_kind': 'body', 'l_body': '(b) A citação ou notificação postal apenas poderá ser '}
23409,{'l_kind': 'body', 'l_body': 'considerada válida quando for recebida pessoalmente '}
