25516,{'l_kind': 'body', 'l_body': '(D) pertence ao Município e ao Estado, mediante prévia '}
25517,{'l_kind': 'body', 'l_body': 'e justa indenização em dinheiro.'}
25518,{'l_kind': 'body', 'l_body': '(e) pertence à União, mediante prévia e justa indeni-'}
25519,{'l_kind': 'body', 'l_body': 'zação em títulos da dívida agrária com cláusula de '}
