25783,{'l_kind': 'body', 'l_body': '(b) O adolescente que atinge os 18 anos de idade deve ser '}
25784,{'l_kind': 'body', 'l_body': 'compulsoriamente liberado da medida socioeducativa '}
25785,{'l_kind': 'body', 'l_body': 'de internação em razão do alcance da maioridade '}
