27179,{'l_kind': 'body', 'l_body': "{'243"}
27180,{'l_kind': 'body', 'l_body': '14. DIREItO PENAl'}
27181,{'l_kind': 'body', 'l_body': 'determina à enfermeira Hortência que troque o frasco de '}
