27697,{'l_kind': 'body', 'l_body': 'se interrompe:'}
27698,{'l_kind': 'body', 'l_body': '(a) enquanto o agente cumpre pena no estrangeiro.'}
27699,{'l_kind': 'body', 'l_body': '(b) durante o tempo em que o condenado está preso por '}
27700,{'l_kind': 'body', 'l_body': 'outro motivo.'}
27701,{'l_kind': 'body', 'l_body': '(C) pela continuação do cumprimento da pena.'}
